import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  Divider,
} from "@mui/material";
import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import ChatCard from "../../components/ChatCard";
import { useLocation, useSearchParams } from "react-router-dom";

const ChatPage = () => {
  const location = useLocation();
  const adventures = useSelector((state) => state.adventurePostsReducer);
  const user = useSelector((state) => state.authReducer.authData?.user);
  const [activeChat, setActiveChat] = useState("");

  return (
    <Container sx={{ padding: 5 }} maxWidth="lg">
      <Typography variant="h5" sx={{ fontWeight: 600 }}>
        Chat Room
      </Typography>
      <Paper
        sx={{
          height: "75vh",
          marginTop: "20px",
          boxShadow:
            "0px 4px 8px 0px rgba(0, 0, 0, 0.2), 0px 6px 20px 0px rgba(0, 0, 0, 0.19)",
          borderRadius: "15px",
          paddingY: "20px",
          paddingX: "10px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Box
          sx={{
            width: "100%",
            height: "100%",
            display: "flex",
          }}
        >
          <Box
            sx={{ display: "flex", flexDirection: "column", marginRight: 1 }}
          >
            <Divider></Divider>
            {adventures.map((adventure) =>
              adventure.adventureParticipants.some(
                (adventureParticipant) =>
                  adventureParticipant?.userId === user?._id
              ) ? (
                <>
                  <ChatCard
                    key={adventure._id}
                    adventure={adventure}
                    activeChat={activeChat}
                    setActiveChat={setActiveChat}
                  />
                  <Divider></Divider>
                </>
              ) : null
            )}
          </Box>
          <Divider orientation="vertical"></Divider>
        </Box>
      </Paper>
    </Container>
  );
};

export default ChatPage;
