import React from "react";
import {
  Card,
  Typography,
  Box,
  Grid,
  CardMedia,
  Divider,
  Skeleton,
} from "@mui/material";
const PostDetailsSkeleton = () => {
  const data = {
    title: "A new Basketball Game",
    details:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam sed erat sed sapien ultrices ultrices sed sed sapien ultrices ultrices sed",
    image:
      "https://images.unsplash.com/photo-1501785888041-af3ef285b470?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8f",
    tags: ["$kdjsfl"],
    creatorName: "Yash",
    createdOn: "2022-02-03",
    Location: "",
  };
  return (
    <Grid container spacing={4}>
      <Grid item xs={12} md={6}>
        <Card
          sx={{
            width: "auto",
            margin: "auto",
            borderRadius: "15px",
          }}
        >
          <Skeleton
            sx={{ height: 350 }}
            animation="wave"
            variant="rectangular"
          />
        </Card>
      </Grid>
      <Grid item xs={12} md={6}>
        <Box
          sx={{
            height: "100%",
            width: "100%",
            display: "flex",
            flexDirection: "column",
            alignItems: "start",
            justifyContent: "center",
            marginBottom: 2,
            marginTop: 2,
          }}
        >
          <Skeleton
            animation="wave"
            style={{ marginTop: 3, marginBottom: 10, marginLeft: 4 }}
            height={20}
            width="50%"
          />
          <Box
            sx={{
              marginTop: 1,
              display: "flex",
            }}
          >
            <Typography sx={{ fontWeight: 600 }}>Creator :</Typography>
            <Typography sx={{ paddingLeft: 1 }} variant="body1">
              {`${data?.creatorName}`}
            </Typography>
          </Box>
          <Box
            sx={{
              marginTop: 1,
              display: "flex",
            }}
          >
            <Typography variant="body1" sx={{ fontWeight: 600 }}>
              Created On :
            </Typography>
            <Typography
              sx={{ paddingLeft: 1 }}
              variant="body1"
            >{`${data.createdOn}`}</Typography>
          </Box>
          <Box
            sx={{
              marginTop: 1,
              display: "flex",
            }}
          >
            <Typography
              sx={{ fontWeight: 600, whiteSpace: "nowrap" }}
              variant="body1"
            >{`Tags : `}</Typography>
            <Typography sx={{ paddingLeft: 1 }}>
              {data?.tags?.join(" ")}
            </Typography>
          </Box>
          <Box
            sx={{
              marginTop: 1,
              display: "flex",
            }}
          >
            <Typography sx={{ whiteSpace: "nowrap", fontWeight: 600 }}>
              {`Location :`}
            </Typography>
            <Typography sx={{ paddingLeft: 1 }} variant="body1">
              {data?.location}
            </Typography>
          </Box>
        </Box>
      </Grid>
      <Grid item sx={12}>
        <Box
          sx={{
            marginTop: 1,
            display: "flex",
          }}
        >
          <Typography sx={{ whiteSpace: "nowrap", fontWeight: 600 }}>
            {`Details :`}
          </Typography>
          <Typography
            textAlign={"justify"}
            sx={{ paddingLeft: 1 }}
            variant="body1"
          >
            {data?.details}
          </Typography>
        </Box>
        <Divider textAlign="left" sx={{ color: "black", marginTop: 2 }}>
          <Typography variant="h6">
            {data?.adventureParticipantsCount} Participant(s)
          </Typography>
        </Divider>
      </Grid>
    </Grid>
  );
};

export default PostDetailsSkeleton;
