import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { Paper, Container, Card, CardMedia } from "@mui/material";

const AdventurePostDetails = () => {
  const location = useLocation();
  const data = location.state?.postData;
  return (
    <>
      <Container maxWidth={"md"}>
        <Paper
          sx={{
            marginTop: "20px",
            boxShadow:
              "0px 4px 8px 0px rgba(0, 0, 0, 0.2), 0px 6px 20px 0px rgba(0, 0, 0, 0.19)",
            borderRadius: "15px",
            padding: "20px",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <h1>{data.title}</h1>
          <Card
            sx={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              borderRadius: "15px",
              height: "100%",
              position: "relative",
            }}
          >
            <CardMedia
              sx={{
                height: 0,
                paddingTop: "56.25%",
                backgroundColor: "rgba(0, 0, 0, 0.5)",
                backgroundBlendMode: "darken",
              }}
              image={
                data.image ||
                "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"
              }
              title={data.title}
            />
          </Card>
          {/* <img
            height={350}
            width={350}
            src={
              `${data.image}` ||
              "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"
            }
            alt={"adventure"}
          /> */}
        </Paper>
      </Container>
    </>
  );
};

export default AdventurePostDetails;
