import {
  Container,
  Paper,
  Typography,
  Grid,
  Card,
  Chip,
  CardMedia,
  TextField,
} from "@mui/material";
import React, { useState } from "react";
import { styles } from "./styles";
const AddPostPage = () => {
  const [tagText, setTagText] = useState("");
  const [tagList, setTagList] = useState([]);
  const handleDeleteTag = (tagName) => (e) => {
    setTagList(tagList.filter((tag) => tag !== tagName));
  };
  return (
    <Container sx={{ paddingTop: 5 }}>
      <Typography sx={{ fontSize: 20 }}>Add Post</Typography>
      <Paper elevation={3} sx={styles.container}>
        <Grid container spacing={3} sx={{ marginLeft: 1 }}>
          <Grid item sm={6}>
            <TextField
              name="adventureName"
              label="Post Caption"
              fullWidth
              variant="outlined"
              type="text"
              autoComplete="off"
            />
          </Grid>
          <Grid item sm={6}>
            <TextField
              name="adventureTags"
              label="Tags"
              fullWidth
              variant="outlined"
              type="text"
              value={tagText}
              onChange={(e) => {
                setTagText(e.target.value);
              }}
              onKeyDown={(e) => {
                if (e.key === "Enter") {
                  setTagList([...tagList, tagText]);
                  setTagText("");
                }
              }}
              autoComplete="off"
            ></TextField>
            {tagList.map((i) => {
              return (
                <Chip
                  label={i}
                  sx={{ marginTop: 1, marginRight: 1 }}
                  onDelete={handleDeleteTag(i)}
                />
              );
            })}
          </Grid>
          <Grid item sm={12}>
            <TextField
              name="adventureDetails"
              label="Post Details"
              fullWidth
              variant="outlined"
              type="text"
              multiline
              rows={4}
              autoComplete="off"
            />
          </Grid>
          <Grid item sm={6}></Grid>
        </Grid>
      </Paper>
    </Container>
  );
};

export default AddPostPage;
