import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import {
  Paper,
  Container,
  Card,
  CardMedia,
  Grid,
  Box,
  Typography,
  Chip,
} from "@mui/material";

const AdventurePostDetails = () => {
  const location = useLocation();
  const data = location.state?.postData;
  return (
    <>
      <Container maxWidth={"md"}>
        <Paper
          sx={{
            marginTop: "20px",
            boxShadow:
              "0px 4px 8px 0px rgba(0, 0, 0, 0.2), 0px 6px 20px 0px rgba(0, 0, 0, 0.19)",
            borderRadius: "15px",
            paddingY: "20px",
            paddingX: "10px",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Grid container spacing={4}>
            <Grid item xs={12} md={6}>
              <Card
                sx={{
                  width: "auto",
                  margin: "auto",
                  borderRadius: "15px",
                }}
              >
                <CardMedia
                  sx={{
                    height: 0,
                    paddingTop: "100%",
                    backgroundColor: "rgba(0, 0, 0, 0.2)",
                    backgroundBlendMode: "darken",
                  }}
                  image={
                    data.image ||
                    "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"
                  }
                  title={data.title}
                />
              </Card>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box
                sx={{
                  height: "100%",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "start",
                  justifyContent: "center",
                  marginBottom: 2,
                  marginTop: 2,
                }}
              >
                <div>
                  <Typography sx={{ fontWeight: 500 }} variant="body">
                    {`${data.title}`}
                  </Typography>
                </div>
                <div>
                  <Typography variant="body1">
                    {`Creator : ${data.creatorName}`}
                  </Typography>
                </div>
                <div>
                  <Typography>
                    {" "}
                    variant="body1" sx=
                    {{ backgroundColor: "red" }}> Created On :
                  </Typography>
                  <Typography variant="body1">{`${data.createdOn}`}</Typography>
                </div>
                <div>
                  <Typography variant="body1">{`Tags : `}</Typography>
                  {data?.tags?.map((i) => {
                    return (
                      <Chip label={i} sx={{ marginTop: 1, marginRight: 1 }} />
                    );
                  })}
                </div>
              </Box>
            </Grid>
          </Grid>
        </Paper>
      </Container>
    </>
  );
};

export default AdventurePostDetails;
