import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import {
  Paper,
  Container,
  Card,
  CardMedia,
  Grid,
  Box,
  Typography,
  Divider,
  Avatar,
  CardActionArea,
  CardContent,
  Button,
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import moment from "moment";
import { addAdventureParticipant } from "../../actions/adventurePosts";
import ConfirmationDialog from "../../components/ConfirmationDialog";

const AdventurePostDetails = () => {
  const [dialogOpen, setDialogOpen] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const user = useSelector((state) => state.authReducer?.authData?.user);
  const data = location.state?.postData;

  const handleParticipate = (e) => {
    setDialogOpen(true);
  };

  const confirmParticipate = () => {
    dispatch(
      addAdventureParticipant(data._id, user?._id, user?.name, navigate)
    );
  };

  useEffect(() => {}, []);
  return (
    <>
      <ConfirmationDialog
        dialogOpen={dialogOpen}
        setDialogOpen={setDialogOpen}
        confirmSubmit={confirmParticipate}
      />
      <Container sx={{ marginBottom: 10 }} maxWidth={"md"}>
        <Paper
          sx={{
            marginTop: "20px",
            boxShadow:
              "0px 4px 8px 0px rgba(0, 0, 0, 0.2), 0px 6px 20px 0px rgba(0, 0, 0, 0.19)",
            borderRadius: "15px",
            paddingY: "20px",
            paddingX: "10px",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Grid container spacing={4}>
            <Grid item xs={12} md={6}>
              <Card
                sx={{
                  width: "auto",
                  margin: "auto",
                  borderRadius: "15px",
                }}
              >
                <CardMedia
                  sx={{
                    height: 0,
                    paddingTop: "100%",
                    backgroundColor: "rgba(0, 0, 0, 0.2)",
                    backgroundBlendMode: "darken",
                  }}
                  image={
                    data?.image ||
                    "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"
                  }
                  title={data?.title}
                />
              </Card>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box
                sx={{
                  height: "100%",
                  width: "100%",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "start",
                  justifyContent: "center",
                  marginBottom: 2,
                  marginTop: 2,
                }}
              >
                <Box sx={{ marginTop: 1, display: "flex" }}>
                  <Typography
                    component={"h6"}
                    variant={"body1"}
                    sx={{ fontWeight: 600, whiteSpace: "nowrap" }}
                  >
                    Name :
                  </Typography>
                  <Typography sx={{ paddingLeft: 1, fontWeight: 500 }}>
                    {` ${data?.title}`}
                  </Typography>
                </Box>
                <Box
                  sx={{
                    marginTop: 1,
                    display: "flex",
                  }}
                >
                  <Typography sx={{ fontWeight: 600 }}>Creator :</Typography>
                  <Typography sx={{ paddingLeft: 1 }} variant="body1">
                    {`${data?.creatorName}`}
                  </Typography>
                </Box>
                <Box
                  sx={{
                    marginTop: 1,
                    display: "flex",
                  }}
                >
                  <Typography variant="body1" sx={{ fontWeight: 600 }}>
                    Created On :
                  </Typography>
                  <Typography
                    sx={{ paddingLeft: 1 }}
                    variant="body1"
                  >{`${moment(data?.createdOn).format(
                    "YYYY-MM-DD"
                  )}`}</Typography>
                </Box>
                <Box
                  sx={{
                    marginTop: 1,
                    display: "flex",
                  }}
                >
                  <Typography
                    sx={{ fontWeight: 600, whiteSpace: "nowrap" }}
                    variant="body1"
                  >{`Tags : `}</Typography>
                  <Typography sx={{ paddingLeft: 1 }}>
                    {data?.tags?.join(" ")}
                  </Typography>
                </Box>
                <Box
                  sx={{
                    marginTop: 1,
                    display: "flex",
                  }}
                >
                  <Typography sx={{ whiteSpace: "nowrap", fontWeight: 600 }}>
                    {`Location :`}
                  </Typography>
                  <Typography sx={{ paddingLeft: 1 }} variant="body1">
                    {data?.location}
                  </Typography>
                </Box>
              </Box>
            </Grid>
            <Grid item sx={12}>
              <Box
                sx={{
                  marginTop: 1,
                  display: "flex",
                }}
              >
                <Typography sx={{ whiteSpace: "nowrap", fontWeight: 600 }}>
                  {`Details :`}
                </Typography>
                <Typography
                  textAlign={"justify"}
                  sx={{ paddingLeft: 1 }}
                  variant="body1"
                >
                  {data?.details}
                </Typography>
              </Box>
              {data?.adventureParticipants?.some(
                (adventureParticipant) =>
                  adventureParticipant.userId === user?._id
              ) ? (
                <Box
                  sx={{
                    padding: 2,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "end",
                  }}
                >
                  <Button onClick={handleParticipate} variant="contained">
                    Cancle Participation
                  </Button>
                </Box>
              ) : (
                <Box
                  sx={{
                    padding: 2,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "end",
                  }}
                >
                  <Button onClick={handleParticipate} variant="contained">
                    Participate
                  </Button>
                </Box>
              )}
              <Divider textAlign="left" sx={{ color: "black", marginTop: 2 }}>
                <Typography variant="h6">
                  {data?.adventureParticipantsCount} Participant(s)
                </Typography>
              </Divider>
            </Grid>

            {data?.adventureParticipants.map((user) => (
              <Grid item xs={12} md={6}>
                <Card
                  sx={{
                    width: "100%",
                  }}
                >
                  <CardActionArea>
                    <CardContent
                      sx={{
                        marginX: 1,
                      }}
                      onClick={() => console.log("Clicked")}
                    >
                      <Box
                        sx={{
                          display: "flex",
                          alignItems: "center",
                          overflow: "hidden",
                        }}
                      >
                        <Avatar>
                          {user.userName.split(" ")[0][0]}
                          {user.userName.split(" ")[1][0]}
                        </Avatar>
                        <Box
                          sx={{
                            marginLeft: 1,
                            display: "block",
                            overflow: "hidden",
                          }}
                        >
                          <Typography noWrap variant={"h6"}>
                            {user?.userName}
                          </Typography>
                          <Typography noWrap>
                            {user?.userId === data?.createdBy
                              ? "creator"
                              : "participant"}
                          </Typography>
                        </Box>
                      </Box>
                    </CardContent>
                  </CardActionArea>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Paper>
      </Container>
    </>
  );
};

export default AdventurePostDetails;
