import { Container, Typography, Button, Stack } from "@mui/material";
import React, { useState } from "react";
import CreatePost from "../../components/CreatePost";
const AddPostPage = () => {
  const [createType, setCreateType] = useState("");
  return (
    <Container sx={{ paddingTop: 5, paddingBottom: 20, width: "100vw" }}>
      <Stack
        sx={{ backgroundColor: "red" }}
        alignContent={"center"}
        direction={"row"}
        spacing={3}
      >
        <Button variant="contained">Create Feed Post</Button>
        <Button variant="outlined">Create Adventure</Button>
      </Stack>
      {createType === "feedpost" && (
        <>
          <Typography variant="h4" sx={{ margin: 2 }}>
            Create Post
          </Typography>

          <CreatePost />
        </>
      )}
      {createType === "adventurepost" && (
        <>
          <Typography variant="h4" sx={{ marginX: 2, marginY: 3 }}>
            Create Adventure
          </Typography>
          <CreatePost />
        </>
      )}
    </Container>
  );
};

export default AddPostPage;
