import React from "react";
import {
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography,
  CardHeader,
  Tooltip,
  Avatar,
  IconButton,
  Skeleton,
} from "@mui/material";
import ThumbUpAltIcon from "@mui/icons-material/ThumbUpAlt";
import DeleteIcon from "@mui/icons-material/Delete";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import moment from "moment";
import { useDispatch, useSelector } from "react-redux";

function stringAvatar(name) {
  return {
    children: `${name.split(" ")[0][0]}${name.split(" ")[1][0]}`,
  };
}

const Post = ({ post, likePost, deletePost, loading }) => {
  const authData = useSelector((state) => state.authReducer.authData);
  const dispatch = useDispatch();
  return (
    <Card
      sx={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        borderRadius: "15px",
        height: "100%",
        position: "relative",
      }}
    >
      <CardHeader
        avatar={
          <Avatar
            alt="User Profile"
            {...stringAvatar(`${authData?.user?.name}`)}
          />
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        sx={{
          "& .MuiCardHeader-content": {
            display: "block",
            overflow: "hidden",
          },
        }}
        title={
          loading ? (
            <Skeleton
              animation="wave"
              height={18}
              width="80%"
              style={{ marginBottom: 6 }}
            />
          ) : (
            <Tooltip title={post.title} arrow placement="top">
              <Typography variant="body1" noWrap sx={{ fontWeight: 500 }}>
                {post.title}
              </Typography>
            </Tooltip>
          )
        }
        subheader={
          loading ? (
            <Skeleton animation="wave" height={10} width="40%" />
          ) : (
            moment(post.createdOn).fromNow()
          )
        }
      />
      {loading ? (
        <Skeleton sx={{ height: 150 }} animation="wave" variant="rectangular" />
      ) : (
        <CardMedia
          sx={{
            height: 0,
            paddingTop: "56.25%",
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            backgroundBlendMode: "darken",
          }}
          image={
            post.image ||
            "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"
          }
          title={post.title}
        />
      )}
      <div
        style={{
          display: "flex",
          flexDirection: "horizontal",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        {/* <Typography variant="h6">{post.createdBy}</Typography> */}
        {/* <Typography sx={{ paddingLeft: 2 }} variant="body2">
          {moment(post.createdOn).fromNow()}
        </Typography>
        <Button style={{ color: "blue" }} size="large">
          <MoreHorizIcon fontSize="medium" />
        </Button> */}
      </div>
      {loading ? (
        <Skeleton
          animation="wave"
          style={{ marginTop: 3, marginBottom: 10, marginLeft: 4 }}
          height={10}
          width="40%"
        />
      ) : (
        <Typography
          sx={{ paddingLeft: 2 }}
          variant="body2"
          color="textSecondary"
          component="h2"
        >
          {post?.tags?.slice(0, 2).map((tag) => `${tag} `)}
        </Typography>
      )}
      {loading ? (
        <div style={{ marginLeft: 4, marginRight: 4 }}>
          <Skeleton animation="wave" height={10} style={{ marginBottom: 3 }} />
          <Skeleton animation="wave" height={10} style={{ marginBottom: 3 }} />
          <Skeleton animation="wave" height={10} style={{ marginBottom: 3 }} />
          <Skeleton animation="wave" height={10} style={{ marginBottom: 20 }} />
        </div>
      ) : (
        <CardContent>
          <Typography
            sx={{
              textAlign: "justify",
              overflow: "hidden",
              textOverflow: "ellipsis",
              display: "-webkit-box",
              WebkitLineClamp: "4",
              WebkitBoxOrient: "vertical",
            }}
            variant="body2"
            color="textSecondary"
            component="p"
          >
            {post.details.substring(0, 150)}
          </Typography>
        </CardContent>
      )}
      {loading ? null : (
        <CardActions
          sx={{
            padding: "0 16px 8px 16px",
            display: "flex",
            justifyContent: "space-between",
          }}
        >
          <Button
            size="small"
            color="primary"
            onClick={() => dispatch(likePost(post._id))}
          >
            <ThumbUpAltIcon fontSize="small" /> Like {post.likeCount}{" "}
          </Button>
          <Button
            size="small"
            color="primary"
            onClick={() => dispatch(deletePost(post._id))}
          >
            <DeleteIcon fontSize="small" /> Delete
          </Button>
        </CardActions>
      )}
    </Card>
  );
};

export default Post;
