import React, { useState } from "react";
import { Grid, CircularProgress, Skeleton } from "@mui/material";
import { useSelector } from "react-redux";
import Post from "../../components/Post";
import {
  likeAdventurePost,
  deleteAdventurePost,
} from "../../actions/adventurePosts";
// import useStyles from './styles';

const HomePage = ({ setCurrentId }) => {
  const posts = useSelector((state) => state.adventurePostsReducer);
  const [loading, setLoading] = useState(true);
  // const classes = useStyles();

  return (
    <Grid
      sx={{
        display: "flex",
        alignItems: "center",
        spacing: 2,
        paddingX: 2,
        paddingTop: 2,
        paddingBottom: 20,
      }}
      // className={classes.container}
      container
      alignItems="stretch"
      spacing={3}
    >
      {posts.map((post) =>
        loading ? (
          <>
            <Skeleton variant="text" sx={{ fontSize: "1rem" }} />

            {/* For other variants, adjust the size with `width` and `height` */}
            <Skeleton variant="circular" width={40} height={40} />
            <Skeleton variant="rectangular" width={210} height={60} />
            <Skeleton variant="rounded" width={210} height={60} />
          </>
        ) : (
          <Grid key={post._id} item xs={12} sm={6} md={4} lg={3} xl={2.4}>
            <Post
              post={post}
              likePost={likeAdventurePost}
              deletePost={deleteAdventurePost}
            />
          </Grid>
        )
      )}
    </Grid>
  );
};

export default HomePage;
