import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  Divider,
} from "@mui/material";
import React, { useState } from "react";
import { useSelector } from "react-redux";
import ChatCard from "../../components/ChatCard";
import { useTheme } from "@mui/styles";
import { alpha } from "@mui/material";

const ChatPage = () => {
  const adventures = useSelector((state) => state.adventurePostsReducer);
  const user = useSelector((state) => state.authReducer.authData?.user);
  const theme = useTheme();
  const [active, setActive] = useState("");
  return (
    <Container maxWidth="lg">
      <Paper
        sx={{
          marginBottom: 20,
          // backgroundColor: alpha(theme.palette.secondary.main, 0.5),
          height: "80vh",
          marginTop: "20px",
          boxShadow:
            "0px 4px 8px 0px rgba(0, 0, 0, 0.2), 0px 6px 20px 0px rgba(0, 0, 0, 0.19)",
          borderRadius: "15px",
          paddingY: "20px",
          paddingX: "10px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography variant="h5" sx={{ fontWeight: 600 }}>
          Chat Room
        </Typography>
        <Grid container>
          <Grid item xs={12} md={4}>
            {adventures.map((adventure) =>
              adventure.adventureParticipants.some(
                (adventureParticipant) =>
                  adventureParticipant?.userId === user?._id
              ) ? (
                <ChatCard
                  key={adventure._id}
                  adventure={adventure}
                  active={active}
                  setActive={setActive}
                />
              ) : null
            )}
          </Grid>
          <Grid
            sx={{
              height: "75vh",
            }}
            item
            xs={12}
            md={8}
          >
            <Box
              sx={{
                marginTop: 2,
                width: "100%",
                height: "75vh",
                overflowY: "scroll",
                display: "flex",
                backgroundColor: alpha(theme.palette.primary.main, 0.7),
              }}
            ></Box>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
};

export default ChatPage;
