import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import {
  Paper,
  Container,
  Card,
  CardMedia,
  Grid,
  Box,
  Typography,
  Chip,
} from "@mui/material";
import moment from "moment";

const AdventurePostDetails = () => {
  const location = useLocation();
  const data = location.state?.postData;
  return (
    <>
      <Container maxWidth={"md"}>
        <Paper
          sx={{
            marginTop: "20px",
            boxShadow:
              "0px 4px 8px 0px rgba(0, 0, 0, 0.2), 0px 6px 20px 0px rgba(0, 0, 0, 0.19)",
            borderRadius: "15px",
            paddingY: "20px",
            paddingX: "10px",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Grid container spacing={4}>
            <Grid item xs={12} md={6}>
              <Card
                sx={{
                  width: "auto",
                  margin: "auto",
                  borderRadius: "15px",
                }}
              >
                <CardMedia
                  sx={{
                    height: 0,
                    paddingTop: "100%",
                    backgroundColor: "rgba(0, 0, 0, 0.2)",
                    backgroundBlendMode: "darken",
                  }}
                  image={
                    data.image ||
                    "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"
                  }
                  title={data.title}
                />
              </Card>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box
                sx={{
                  height: "100%",
                  width: "100%",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "start",
                  justifyContent: "center",
                  marginBottom: 2,
                  marginTop: 2,
                }}
              >
                <div style={{ marginTop: 1, display: "flex" }}>
                  <Typography variant="body1" sx={{ fontWeight: 600 }}>
                    Adventure Name :
                  </Typography>
                  <Typography
                    sx={{ paddingLeft: 1, fontWeight: 500 }}
                    variant="body"
                  >
                    {` ${data.title}`}
                  </Typography>
                </div>
                <div style={{ marginTop: 1, display: "flex" }}>
                  <Typography variant="body1" sx={{ fontWeight: 600 }}>
                    Creator :
                  </Typography>
                  <Typography sx={{ paddingLeft: 1 }} variant="body1">
                    {`${data.creatorName}`}
                  </Typography>
                </div>
                <div style={{ marginTop: 1, display: "flex" }}>
                  <Typography variant="body1" sx={{ fontWeight: 600 }}>
                    Created On :
                  </Typography>
                  <Typography
                    sx={{ paddingLeft: 1 }}
                    variant="body1"
                  >{`${moment(data.createdOn).format(
                    "YYYY-MM-DD"
                  )}`}</Typography>
                </div>
                <div
                  style={{
                    marginTop: 1,
                    display: "flex",
                    alignItems: "center",
                  }}
                >
                  <Typography
                    sx={{ fontWeight: 600 }}
                    variant="body1"
                  >{`Tags : `}</Typography>
                  {data?.tags?.map((i) => {
                    return (
                      <Typography
                        sx={{ paddingLeft: 0.4 }}
                        variant="body1"
                      >{`${i}`}</Typography>
                    );
                  })}
                </div>
                <div style={{ marginTop: 1, display: "flex" }}>
                  <Typography sx={{ fontWeight: 600 }}>
                    {`Location :`}
                  </Typography>
                  <Typography
                    sx={{ textAlign: "center", paddingLeft: 1 }}
                    variant="body1"
                  >
                    {data?.location}
                  </Typography>
                </div>
              </Box>
            </Grid>
          </Grid>
        </Paper>
      </Container>
    </>
  );
};

export default AdventurePostDetails;
