import {
  Container,
  Paper,
  Typography,
  Grid,
  Card,
  Chip,
  CardMedia,
  TextField,
} from "@mui/material";
import React, { useState } from "react";
import { styles } from "./styles";
const AddPostPage = () => {
  const [tagText, setTagText] = useState("");
  const [tagList, setTagList] = useState([]);
  const handleDeleteTag = () => {};
  return (
    <Container sx={{ paddingTop: 5, paddingLeft: 2 }}>
      <Typography sx={{ fontSize: 20 }}>Add Post</Typography>
      <Paper elevation={3} sx={styles.container}>
        <Grid container spacing={3} sx={{ marginLeft: 1 }}>
          <Grid item sm={6}>
            <TextField
              name="adventureName"
              label="Post Caption"
              fullWidth
              variant="outlined"
              autoFocus
              type="text"
              autoComplete="off"
            />
          </Grid>
          <Grid item sm={6}>
            <TextField
              name="adventureTags"
              label="Tags"
              fullWidth
              variant="outlined"
              type="text"
              value={tagText}
              onChange={(e) => {
                setTagText(e.target.value);
              }}
              onKeyDown={(e) => {
                if (e.key === "Enter") {
                  e.preventDefault();
                  setTagList([...tagList, tagText]);
                  setTagText("");
                }
              }}
            >
              {tagList.map((i) => {
                return (
                  <Chip
                    label={"he"}
                    sx={{ marginTop: 1 }}
                    onDelete={() => {}}
                  />
                );
              })}
            </TextField>
          </Grid>
          <Grid item sm={12}>
            <TextField
              name="adventureDetails"
              label="Post Details"
              fullWidth
              variant="outlined"
              type="text"
              multiline
              rows={4}
            />
          </Grid>
          <Grid item sm={6}></Grid>
        </Grid>
      </Paper>
    </Container>
  );
};

export default AddPostPage;
